mapper.select:
SELECT m.*, b.BRANCH_NAME, l.LOCAL_NAME FROM MEMBER m INNER JOIN branch b ON m.BRANCH_NO = b.BRANCH_NO INNER JOIN LOCAL l ON l.LOCAL_NO = b.LOCAL_NO
WHERE m.branch_no = #{branch_no}
ORDER BY b.branch_no DESC

SELECT m.*, b.BRANCH_NAME, l.LOCAL_NAME FROM MEMBER m INNER JOIN branch b ON m.BRANCH_NO = b.BRANCH_NO INNER JOIN LOCAL l ON l.LOCAL_NO = b.LOCAL_NO WHERE instr(m.member_name, #{member_name}) > 0
AND m.branch_no = #{branch_no}
ORDER BY b.branch_no DESC
