mapper.select:
SELECT p.*, pp.*,l.*,TO_CHAR(p.PAY_HIS_DATE ,'YYYY-MM-DD')AS PAY_HIS_DATE2 FROM pay_his p LEFT OUTER JOIN license l ON p.LICENSE_NO = l.LICENSE_NO LEFT OUTER JOIN MEMBER m ON p.MEMBER_NO = m.MEMBER_NO LEFT OUTER JOIN pay_point pp ON p.tid_no = pp.tid_no WHERE m.member_no=#{member_no}
AND p.PAY_HIS_DATE BETWEEN TO_DATE('${start} 00:00:00', 'yyyy-mm-dd hh24:mi:ss') AND TO_DATE('${finish} 23:59:59', 'yyyy-mm-dd hh24:mi:ss')
AND TO_CHAR(p.PAY_HIS_DATE, 'yyyy-mm-dd') = #{start}
ORDER BY p.PAY_HIS_DATE DESC
